mapper.update:
		update house set front_picture =#{path} where
		house_id=#{houseId} and
		user_id=#{userId}

mapper.select:
		select count(1) from
		house left join house_detail
		on house.house_id =
		house_detail.house_id
		
			
				and city_en_name=#{searchDto.cityEnName}
			

			
				and subway_id=#{searchDto.subwayId}
			
			
				and subway_name=#{searchDto.subwayName}
			
			
				and room=#{searchDto.room}
			

			
				and direction = #{searchDto.direction}
			

			
				and region_en_name =#{searchDto.regionEnName}
			

			
				and price between #{searchDto.priceMin} and
				#{searchDto.priceMax}
			
			
				and price between #{searchDto.priceMin} and 100000000
			

			
				and price between 0 and #{searchDto.priceMax}
			

			
				and price between #{searchDto.areaMin} and
				#{searchDto.areaMax}
			
			
				and price between #{searchDto.areaMin} and 100000
			

			
				and price between 0 and #{searchDto.areaMax}
			
			and status = 1

		select
		house.house_id,
		title,
		price,
		room,
		area,
		floor,
		city_cn_name,
		total_floor,
		front_picture,
		district,
		last_update_time,
		build_year,
		parlour,
		direction,
		distance_to_subway,
		subway_name,
		subway_station_name
		from
		house left join house_detail
		on house.house_id =
		house_detail.house_id
		
			
				and city_en_name=#{searchDto.cityEnName}
			

			
				and subway_id=#{searchDto.subwayId}
			
			
				and subway_name=#{searchDto.subwayName}
			
			
				and room=#{searchDto.room}
			

			
				and direction = #{searchDto.direction}
			

			
				and region_en_name =#{searchDto.regionEnName}
			

			
				and price between #{searchDto.priceMin} and
				#{searchDto.priceMax}
			
			
				and price between #{searchDto.priceMin} and 100000000
			

			
				and price between 0 and #{searchDto.priceMax}
			

			
				and price between #{searchDto.areaMin} and
				#{searchDto.areaMax}
			
			
				and price between #{searchDto.areaMin} and 100000
			

			
				and price between 0 and #{searchDto.areaMax}
			

			and status = 1
		

		order by ${searchDto.orderBy} ${searchDto.orderDirection}
		limit
		#{rowIndex},#{pageSize}

select house.house_id, title, price, room, area, floor, city_cn_name, total_floor, front_picture, district, last_update_time, build_year, parlour, direction, distance_to_subway, subway_name, subway_station_name from house left join house_detail on house.house_id = house_detail.house_id where house.house_id in (
#{houseIds}
)
